import styled from 'styled-components'

export const Container = styled.div`
  position: relative;
  background: #fff;
  border-radius: 5px;
  margin-bottom: 10px;
  padding: 15px;
  box-shadow: 0 1px 4px 0 rgba(192, 208, 230, 0.8);

  header {
    position: absolute;
  }

  img {
    width: 24px;
    height: 24px;
    border-radius: 5px;
    margin-top: 5px;
  }
`

export const Label = styled.span``
